import React, { useState } from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import Container from "react-bootstrap/Container";
import logo from "../Assets/logo2.png";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import { CgGitFork } from "react-icons/cg";
import { ImBlog } from "react-icons/im";
import { BrowserRouter } from "react-router-dom";
import LanguageDropDown from "./LanguageDropDown";
import { useTranslation } from 'react-i18next';

import {
  AiFillStar,
  AiOutlineHome,
  AiOutlineFundProjectionScreen,
  AiOutlineUser,
} from "react-icons/ai";

import { CgFileDocument } from "react-icons/cg";

function NavBar() {

  const [expand, updateExpanded] = useState(false);
  const [navColour, updateNavbar] = useState(false);
  const { t } = useTranslation('common');

  function scrollHandler() {
    if (window.scrollY >= 20) {
      updateNavbar(true);
    } else {
      updateNavbar(false);
    }
  }

  window.addEventListener("scroll", scrollHandler);

  return (

    <Navbar
      expanded={expand}
      fixed="top"
      expand="md"
      className={navColour ? "sticky" : "navbar"}
    >
      <Container>
        <Navbar.Brand href="/">
          <img src={logo} className="img-fluid logo" alt="brand" />
        </Navbar.Brand>
        <Navbar.Toggle
          aria-controls="responsive-navbar-nav"
          onClick={() => {
            updateExpanded(expand ? false : "expanded");
          }}
        >
          <span></span>
          <span></span>
          <span></span>
        </Navbar.Toggle>
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="ml-auto" defaultActiveKey="#home">
            <Nav.Item>
              <Nav.Link as={Link} to="/" onClick={() => updateExpanded(false)}>
                <AiOutlineHome style={{ marginBottom: "2px" }} /> {t("Home")}
              </Nav.Link>
            </Nav.Item>

            <Nav.Item>
              <Nav.Link
                as={Link}
                to="/about"
                onClick={() => updateExpanded(false)}
              >
                <AiOutlineUser style={{ marginBottom: "2px" }} /> {t("About")}
              </Nav.Link>
            </Nav.Item>

            <Nav.Item>
              <Nav.Link
                as={Link}
                to="/project"
                onClick={() => updateExpanded(false)}
              >
                <AiOutlineFundProjectionScreen
                  style={{ marginBottom: "2px" }}
                />{" "}
                {t("Projects & Publications")}

              </Nav.Link>
            </Nav.Item>



            <Nav.Item>
              <Nav.Link
                as={Link}
                to="/resume"
                onClick={() => updateExpanded(false)}
              >
                <CgFileDocument style={{ marginBottom: "2px" }} /> {t("Resume")}
              </Nav.Link>
            </Nav.Item>

            <Nav.Item>
              <LanguageDropDown/>
            </Nav.Item>

            {/*<Nav.Item>*/}
            {/* <Nav.Link*/}
            {/*  href="http://localhost:3000/pwa/"*/}
            {/* target="_blank"*/}
            {/* rel="noreferrer"*/}
            {/* >*/}
             {/*<ImBlog style={{ marginBottom: "2px" }} /> Blogs*/}
           {/*  </Nav.Link>*/}
           {/*</Nav.Item>*/}


          {/*  <Nav.Item className="fork-btn">*/}
          {/*    <Button*/}
          {/*      href="https://github.com/JunDSinfo"*/}
          {/*      target="_blank"*/}
          {/*      className="fork-btn-inner"*/}
          {/*    >*/}
          {/*      <CgGitFork style={{ fontSize: "1.2em" }} />{" "}*/}
          {/*      <AiFillStar style={{ fontSize: "1.1em" }} />*/}
          {/*    </Button>*/}
          {/*  </Nav.Item>*/}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
